package Testsimple;

public class doWhileDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 10;
		do {
			System.out.println(i);
			i++;
		}while(i<20);
		
		System.out.println("********");
		int j = 30;
		do {
			System.out.println(j);
			j--;
		}while(j>20);
	}

}
